import base64


def stop_app():
    set_time(10)
    app.stop()


def set_time(t, p=None):
    # 0xffffffff option for never
    if not p:
        subprocess.call(f"powercfg -change -monitor-timeout-ac {t}")
        subprocess.call(f"powercfg -change -monitor-timeout-dc {t}")
    else:
        subprocess.call(f"powercfg -change -monitor-timeout-{p} {t}")



app = pystray.Icon(
    name="Sleep Toggle",
    title="Sleep Toggle",
    # icon=Image.open("sleep.jpg"),
    icon=Image.open(io.BytesIO(base64.b64decode(""))),
    # "Sleep Icon" by Army Medicine is licensed under CC BY 2.0
    menu=pystray.Menu(
        pystray.MenuItem(
            text="Sleep Toggle",
            action=None,
        ),
        pystray.MenuItem(
            text="Set to 10 minutes",
            # action=lambda: button_press(1),
            action=lambda: set_time(10),
            # checked=get_state(1),
            radio=True
        ),
        pystray.MenuItem(
            text="Set to 3 hours",
            # action=button_press(2),
            action=lambda: set_time(180),
            # checked=get_state(2),
            radio=True
        ),
        pystray.MenuItem(
            text="Set to forever",
            # action=button_press(3),
            action=lambda: set_time('0xffffffff'),
            # checked=get_state(3),
            radio=True
        ),
        pystray.MenuItem(
            text='Quit',
            action=lambda: stop_app(),
            checked=None
        )
    )
)

app.run()

